import Layout from '../components/Layout';
import { useState, ChangeEvent } from 'react';

const colorName = () => {
  const [colorName, setColorName] = useState('');
  const [color, setColor] = useState('');

  let timer: number;

  function getData(value: string) {
    const time = 1000;
    clearTimeout(timer);

    timer = window.setTimeout(() => {
      if (value.match(/[0-9a-fA-F]+/)) {
        setColor(value);
        console.log(color);

        fetch(`https://www.thecolorapi.com/id?hex=${value}`)
          .then((res) => res.json())
          .then((data) => setColorName(data.name.value));
      } else console.log('error');
    }, time);
  }

  return (
    <Layout>
      <h1 className="title">Get a random color</h1>

      <div className="input--container my-5">
        <input
          type="text"
          className="input"
          placeholder="Your color"
          maxLength={6}
          onChange={(e) => getData(e.target.value)}
          required
        />
        <span className="hashtag">
          #<span className="color--bar" style={{ background: color }}></span>
        </span>
      </div>

      <p className="desc">
        Your color's name: <span className="color--name">{colorName}</span>
      </p>
    </Layout>
  );
};

export default colorName;
