import Layout from '../components/Layout';
import { useState, ChangeEvent } from 'react';

const colorName = () => {
  const [color, setColor] = useState('');

  let timeout: number;

  const getData = async (e: ChangeEvent<HTMLInputElement>) => {
    // e.persist();
    // clearTimeout(timeout);

    // const value = e.target.value;
    // setColor(value);

    // timeout = window.setTimeout(async () => {
    if (color.match(/[0-9a-fA-F]+/)) {
      const res = await fetch(`https://www.thecolorapi.com/id?hex=${color}`);
      const data = await res.json();

      console.log(data.name.value);
    } else console.log('error');
    // }, 1000);
  };

  return (
    <Layout>
      <h1 className="title">Get a random color</h1>

      <div className="input--container my-5">
        <input
          type="text"
          className="input"
          placeholder="Your color"
          maxLength={6}
          onChange={(e) => {
            setColor(e.target.value);
          }}
          required
        />
        <span className="hashtag">#</span>
      </div>

      <p className="desc">
        Your color's name:{' '}
        <span className="color--name" style={{ color }}>
          PURPLE
        </span>
      </p>
    </Layout>
  );
};

export default colorName;
