import Layout from '../components/Layout';
import { useState, ChangeEvent } from 'react';

const colorName = () => {
  const [color, setColor] = useState('');

  function getData(value: string) {
    if (value.match(/[0-9a-fA-F]+/)) {
      fetch(`https://www.thecolorapi.com/id?hex=${value}`)
        .then((res) => res.json())
        .then((data) => setColor(data.name.value));
    } else console.log('error');
  }

  return (
    <Layout>
      <h1 className="title">Get a random color</h1>

      <div className="input--container my-5">
        <input
          type="text"
          className="input"
          placeholder="Your color"
          maxLength={6}
          onChange={(e) => getData(e.target.value)}
          required
        />
        <span className="hashtag">#</span>
      </div>

      <p className="desc">
        Your color's name: <span className="color--name">{color}</span>
      </p>
    </Layout>
  );
};

export default colorName;
