import Layout from '../components/Layout';
import { useState, ChangeEvent } from 'react';

const colorName = () => {
  const [state, setState] = useState('');

  let timeout: number;

  const getData = (e: ChangeEvent<HTMLInputElement>) => {
    e.persist();
    clearTimeout(timeout);

    const value = e.target.value;

    timeout = window.setTimeout(async () => {
      if (value.match(/[0-9a-fA-F]+/)) {
        const res = await fetch(`https://www.thecolorapi.com/id?hex=${value}`);
        const data = await res.json();

        console.log(data.name.value);
      } else console.log('error');
    }, 1000);
  };

  return (
    <Layout>
      <h1 className="title">Get a random color</h1>

      <div className="input--container my-5">
        <input
          type="text"
          className="input"
          placeholder="Your color"
          maxLength={6}
          onChange={getData}
          required
        />
        <span className="hashtag">#</span>
      </div>

      <p>Your color's name: PURPLE</p>
    </Layout>
  );
};

export default colorName;
