import Layout from '../components/Layout';
import Link from 'next/link';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react';
import { stringify } from 'querystring';

const random: React.FC = () => {
  const [color, setColor] = useState({
    color: '',
    contrast: '',
  });

  const handleColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }

    const contrast = getContrastYIQ(color);

    setColor({
      color,
      contrast,
    });

    console.log(contrast);

    // navigator.clipboard.writeText(color);
  };

  const getContrastYIQ = (hexcolor: string) => {
    let r = parseInt(hexcolor.substr(0, 2), 16);
    let g = parseInt(hexcolor.substr(2, 2), 16);
    let b = parseInt(hexcolor.substr(4, 2), 16);
    let yiq = (r * 299 + g * 587 + b * 114) / 1000;

    return yiq >= 128 ? '#343a40' : '#fff';
  };

  return (
    <Layout style={{ height: '100vh' }}>
      <Link href="/">
        <div className="back--container random">
          <FontAwesomeIcon icon={faArrowLeft} className="arrow--icon" />
          <span>Back to home</span>
        </div>
      </Link>

      <div
        className="fullscreen"
        onClick={() => handleColor()}
        style={{
          background: `#${color['color']}`,
        }}
      >
        <div className="mid--container" style={{ color: color['contrast'] }}>
          <h1 className="title mb-4">{`#${color['color']}`}</h1>
          <p className="desc">Left click: Change color</p>
          <p className="desc">Right click: Copy color code</p>
        </div>
      </div>
    </Layout>
  );
};

export default random;
