import Layout from '../components/Layout';
import Link from 'next/link';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft } from '@fortawesome/free-solid-svg-icons';
import { useState } from 'react';

const random: React.FC = () => {
  const [color, setColor] = useState('');

  const handleColor = () => {
    const letters = '0123456789ABCDEF';
    let result = '';
    for (let i = 0; i < 6; i++) {
      result += letters[Math.floor(Math.random() * 16)];
    }

    return setColor(result);

    // navigator.clipboard.writeText(color);
  };

  return (
    <Layout style={{ height: '100vh' }}>
      <Link href="/">
        <div className="back--container random">
          <FontAwesomeIcon icon={faArrowLeft} className="arrow--icon" />
          <span>Back to home</span>
        </div>
      </Link>

      <div
        className="fullscreen"
        onClick={() => handleColor()}
        style={{ background: `#${color}` }}
      >
        <div className="mid--container">
          <h1 className="title mb-4">{`#${color}`}</h1>
          <p className="desc">Left click: Change color</p>
          <p className="desc">Right click: Copy color code</p>
        </div>
      </div>
    </Layout>
  );
};

export default random;
