import Layout from '../components/Layout';
import { useState, ChangeEvent } from 'react';

const colorName = () => {
  const [color, setColor] = useState('');

  if (color.match(/[0-9a-fA-F]+/)) {
    fetch(`https://www.thecolorapi.com/id?hex=${color}`)
      .then((res) => res.json())
      .then((data) => console.log(data.name.value));
  } else console.log('error');

  return (
    <Layout>
      <h1 className="title">Get a random color</h1>

      <div className="input--container my-5">
        <input
          type="text"
          className="input"
          placeholder="Your color"
          maxLength={6}
          onChange={(e) => {
            return setColor(e.target.value);
          }}
          required
        />
        <span className="hashtag">#</span>
      </div>

      <p className="desc">
        Your color's name:{' '}
        <span className="color--name" style={{ color }}>
          PURPLE
        </span>
      </p>
    </Layout>
  );
};

export default colorName;
